function save_options(){var a=document.getElementById("enable");if(a.value==="false"){a.value="true";a.textContent="Disable"}else{a.value="false";a.textContent="Enable"}var b=document.getElementById("enable").value;chrome.storage.sync.set({enabled:b},function(){var c=document.getElementById("status");c.textContent="Options saved.";setTimeout(function(){c.textContent=""},750)})}function restore_options(){chrome.storage.sync.get({enabled:"true"},function(b){var a=document.getElementById("enable");a.value=b.enabled;a.value==="false"?a.textContent="Enable":a.textContent="Disable"})}document.addEventListener("DOMContentLoaded",restore_options);document.getElementById("enable").addEventListener("click",function(){save_options();chrome.tabs.query({active:true,currentWindow:true},function(a){chrome.tabs.update(a[0].id,{url:a[0].url})})});